                    <!-- multi-step form -->
                    <script>
                        function app() {
                            return {
                                step: 1, 
                                passwordStrengthText: '',
                                togglePassword: false,

                                image: 'data:image/jpeg;base64,',
                                password: '',
                                gender: 'Male',

                                checkPasswordStrength() {
                                    var strongRegex = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})");
                                    var mediumRegex = new RegExp("^(((?=.*[a-z])(?=.*[A-Z]))|((?=.*[a-z])(?=.*[0-9]))|((?=.*[A-Z])(?=.*[0-9])))(?=.{6,})");

                                    let value = this.password;

                                    if (strongRegex.test(value)) {
                                        this.passwordStrengthText = "Strong password";
                                    } else if(mediumRegex.test(value)) {
                                        this.passwordStrengthText = "Could be stronger";
                                    } else {
                                        this.passwordStrengthText = "Too weak";
                                    }
                                }
                            }
                        }
                    </script>

                    <!-- insert quote to the textarea -->
                    <script>
                        function addText(event) { 
                            var targ = event.target.innerText || event.innerText;

                            document.getElementById("myeditorinstance").value += targ.textContent || targ.innerText;

                            // Inserts some HTML contents at the current selection
                            tinymce.activeEditor.selection.setContent(targ);                  
                        }

                    </script>

                    <!-- autosave the draft -->
                    <script>  
                        var timeoutId;
                            // tinyMCE.on('body propertychange change', function() {
                                // document.getElementById("myeditorinstance").on('input propertychange change', function() {
                            $('form textarea').on('input propertychange change', function() {
                                // console.log('Textarea Change');
                                
                                clearTimeout(timeoutId);
                                timeoutId = setTimeout(function() {
                                    // Runs 1 second (1000 ms) after the last change    
                                    saveToDB();
                                }, 5000);
                            });

                            function saveToDB()
                            {
                                console.log('Saving to the db');
                                form = $('.draftForm');
                                $.ajaxSetup({
                                    headers: {
                                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                                    }
                                });
                                $.ajax({
                                    url: "{{url('/draft')}}",
                                    type: "POST",
                                    data: form.serialize(), // serializes the form's elements.
                                    beforeSend: function(xhr) {
                                        // Let them know we are saving
                                        $('.form-status-holder').html('Saving...');
                                        console.log('it work so far');
                                    },
                                    success: function(data) {
                                        var jqObj = jQuery(data); // You can get data returned from your ajax call here. ex. jqObj.find('.returned-data').html()
                                        // // Now show them we saved and when we did
                                        var d = new Date();
                                        $('.form-status-holder').html('Saved! Last: ' + d.toLocaleTimeString());
                                        console.log('it work');
                                    },
                                    error: function (data) {
                                        console.log(data);
                                    }
                                });
                            }

                            // This is just so we don't go anywhere  
                            // and still save if you submit the form
                            $('.draftForm').submit(function(e) {
                                saveToDB();
                                e.preventDefault();
                            }); 
                    </script>

                    <!-- abstract maxlength -->
                    <script>
                        window.onload = function() { 
                            var txts = document.getElementsByTagName('draftAbstract'); 

                            for(var i = 0, l = txts.length; i < l; i++) {
                                if(/^[0-9]+$/.test(txts[i].getAttribute("maxlength"))) { 
                                var func = function() { 
                                    var len = parseInt(this.getAttribute("maxlength"), 10); 

                                    if(this.value.length > len) { 
                                    alert('Maximum length exceeded: ' + len); 
                                    this.value = this.value.substr(0, len); 
                                    return false; 
                                    } 
                                }

                                txts[i].onkeyup = func;
                                txts[i].onblur = func;
                                } 
                            };

                            }
                    </script>

                    <!-- random genrator -->
                    <script type="text/javascript">

                        function getRandomNumber() {
                            return Math.random().toString(36).substr(7, 14);
                        }

                        window.onload = function() {
                            const rand = getRandomNumber(0, 100);
                            document.getElementById("uid").value = rand;
                            // $('#uid').html(rand);

                        };
                        
                    </script>

